package person.seven.aes;/** * @description: * @author: liuqi create on 2018/7/19 **/public class Test {    private static final Object lock = new Object();    public static void main(String[] args) throws InterruptedException {        Thread A = new Thread(new Runnable() {            @Override            public void run() {                synchronized (lock) {                    try {                        System.out.println("before wait");                        lock.wait();                        System.out.println("after wait");                    } catch (InterruptedException e) {                        Thread.currentThread().interrupt();                    }                    System.out.println("a executed by notify");                }            }        });        System.out.println("main thread");        A.start();        Thread.sleep(2000);        synchronized (lock) {            lock.notify();        }    }}