package person.seven.aop.log;import java.lang.annotation.*;/** * @description: * @author: liuqi create on 2018/7/4 **/@Documented@Retention(RetentionPolicy.RUNTIME)@Target({ElementType.PARAMETER, ElementType.METHOD})public @interface Log {    /** 要执行的操作类型比如：add操作 **/    public String operationType() default "";    /** 要执行的具体操作比如：添加用户 **/    public String operationName() default "";}