package person.seven.aop.log;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;/** * @description: * @author: liuqi create on 2018/7/4 **/@RestController@RequestMapping("userController")public class UserController {    @RequestMapping("testAOP")    @Log(operationType="add操作:",operationName="添加用户")    public void testAOP(String userName,String password){        System.out.println(String.format(" 保存用户： %s  密码：%s",userName,password));    }    @RequestMapping("delete")    @Log(operationType="delete操作:",operationName="删除用户")    public void delete(String userName,String password){        System.out.println(String.format(" 删除用户： %s  密码：%s",userName,password));    }}