package person.seven.base;/** * @description: * @author: liuqi create on 2019/1/7 **/public class Demo {    public static void main(String[] args) {        Person person = new Person("zhangsan");        unchange(person);        System.out.println(person.getName());        change(person);        System.out.println(person.getName());    }    private static void unchange(Person person) {        person = new Person("lisi");    }    private static void change(Person person) {        person.setName("lisi");    }}class Person{    private String name;    public Person(String name) {        this.name = name;    }    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }}