package person.seven.designMode.FilterPattern;import java.util.List;/** * @description: * @author: liuqi create on 2018/8/10 **/public class OrCriteria extends TypeCriteria {    private Criteria criteria;    private Criteria otherCriteria;    public OrCriteria(Criteria criteria, Criteria otherCriteria) {        this.criteria = criteria;        this.otherCriteria = otherCriteria;    }    @Override    public List<Person> meetCriteria(List<Person> persons) throws Exception {        List<Person> firstCriteriaItems = criteria.meetCriteria(persons);        List<Person> otherCriteriaItems = otherCriteria.meetCriteria(persons);        for (Person person : otherCriteriaItems) {            if(!firstCriteriaItems.contains(person)){                firstCriteriaItems.add(person);            }        }        return firstCriteriaItems;    }}