package person.seven.designMode.FilterPattern;import com.google.common.collect.Lists;import java.lang.reflect.Field;import java.util.List;/** * @description: * @author: liuqi create on 2018/8/10 **/public abstract class TypeCriteria implements Criteria{    @Override    public List<Person> meetCriteria(List<Person> persons,TypeEnum type,String filedName) throws Exception {        List<Person> singlePersons = Lists.newArrayList();        for (Person person : persons) {            Field field = Person.class.getDeclaredField(filedName);            field.setAccessible(true);            if(field.get(person).toString().equalsIgnoreCase(type.getValues())){                singlePersons.add(person);            }        }        return singlePersons;    }}