package person.seven.designMode.behavior.ChainofResponsibilityPattern;/** * @description: * @author: liuqi create on 2018/8/15 **/public abstract class AbstractLogger {    protected Integer level;    private AbstractLogger succeeder;    public AbstractLogger() {    }    public AbstractLogger(AbstractLogger succeeder) {        this.succeeder = succeeder;    }    public void printLog(Integer level, String message){        if(level <= this.level){            wirte(message);        }else if(succeeder != null){           succeeder.printLog(level,message);        }else{            System.out.println(String.format("这是啥等级呀，这个打印不出来了:%s",message));        }    }    protected abstract void wirte(String message);    public void setSucceeder(AbstractLogger succeeder) {        this.succeeder = succeeder;    }}