package person.seven.designMode.behavior.ChainofResponsibilityPattern;/** * @description: * @author: liuqi create on 2018/8/15 **/public class ChainoFactory {    public static AbstractLogger getLoggerChain(){        AbstractLogger consoleLogger = new ConsoleLogger();        AbstractLogger debugLogger = new DebugLogger();        AbstractLogger infoLogger = new InfoLogger();        AbstractLogger errorLogger = new ErrorLogger();        consoleLogger.setSucceeder(debugLogger);        debugLogger.setSucceeder(infoLogger);        infoLogger.setSucceeder(errorLogger);        return consoleLogger;    }}