package person.seven.designMode.behavior.ChainofResponsibilityPattern;/** * @description: * @author: liuqi create on 2018/8/15 **/public class ConsoleLogger extends AbstractLogger{    public ConsoleLogger() {        this.level = LevelConstant.CONSOLE;    }    @Override    protected void wirte(String message) {        System.out.println(String.format("CONSOLE 控制台打印日志：%s",message));    }}