package person.seven.designMode.behavior.ChainofResponsibilityPattern;/** * @description: * @author: liuqi create on 2018/8/15 **/public class DebugLogger extends AbstractLogger{    public DebugLogger() {        this.level = LevelConstant.DEBUG;    }    public DebugLogger(AbstractLogger succeeder) {        super(succeeder);    }    @Override    protected void wirte(String message) {        System.out.println(String.format("DEBUG 控制台打印日志：%s",message));    }}