package person.seven.designMode.behavior.ChainofResponsibilityPattern;/** * @description: * @author: liuqi create on 2018/8/15 **/public class ErrorLogger extends AbstractLogger{    public ErrorLogger() {        this.level = LevelConstant.ERROR;    }    @Override    protected void wirte(String message) {        System.out.println(String.format("ERROR 控制台打印日志：%s",message));    }}