package person.seven.designMode.behavior.ChainofResponsibilityPattern;/** * @description: * @author: liuqi create on 2018/8/15 **/public class InfoLogger extends AbstractLogger{    public InfoLogger() {        this.level = LevelConstant.INFO;    }    @Override    protected void wirte(String message) {        System.out.println(String.format("INFO 控制台打印日志：%s",message));    }}