package person.seven.designMode.behavior.InterpreterPattern;/** * @description: * @author: liuqi create on 2018/8/17 **/public class TerminalExpression implements Expression{    private String context;    public TerminalExpression(String context) {        this.context = context;    }    @Override    public boolean interpret(String context) {        if(context.contains(this.context)){            return true;        }        return false;    }}