package person.seven.designMode.behavior.MementoPattern;import com.google.common.collect.Lists;import java.util.List;/** * @description: * @author: liuqi create on 2018/8/14 **/public class CareTaker {    private List<Memento> mementoList = Lists.newArrayList();    public void add(Memento memento){        mementoList.add(memento);    }    public Memento get(Integer index){        return mementoList.get(index);    }    public List<Memento> getMementoList() {        return mementoList;    }}