package person.seven.designMode.behavior.MementoPattern;import java.util.List;/** * @description: * @author: liuqi create on 2018/8/14 **/public class Originator {    private String state;    public String getState() {        return state;    }    public void setState(String state) {        this.state = state;    }    public Memento saveStateToMemento(){        return new Memento(state);    }    public String getStatsFromMemento(Memento memento){        return memento.getState();    }    public void printAllMementoStatus(List<Memento> list){        list.stream().forEach(memento -> {            System.out.println(memento.getState());        });    }}