package person.seven.designMode.behavior.NullObjectPattern;/** * @description: * @author: liuqi create on 2018/8/15 **/public class CustomerFactory {    public static final String[] names = {"Rob", "Joe", "Julie"};    public static AbstractCustomer getCustomer(String name){        for (int i = 0; i < names.length ; i++) {            if(names[i].equals(name)){                return new RealCustomer(name);            }        }        return new NullCustomer();    }}