package person.seven.designMode.behavior.ObserverPattern;import com.google.common.collect.Lists;import java.util.List;/** * @description: * @author: liuqi create on 2018/8/13 **/public class Subject {    private List<Observer> observers = Lists.newArrayList();    private int state;    public int getState() {        return state;    }    public void setState(int state) {        this.state = state;        notifyAllObservers();    }    public void attach(Observer observer){        observers.add(observer);    }    public void notifyAllObservers(){        for (Observer observer : observers) {            observer.update();        }    }}