package person.seven.designMode.behavior.StrategyPattern;/** * @description: 策略模式 * 在策略模式（Strategy Pattern）中，一个类的行为或其算法可以在运行时更改。这种类型的设计模式属于行为型模式。 * @author: liuqi create on 2018/8/16 **/public class Demo {    public static void main(String[] args) {        int number1 = 10;        int number2= 5;        Context context = new Context();        Strategy add = new AddOperate();        context.setStrategy(add);        System.out.println("添加Add策略后返回结果："+context.doOperate(number1,number2));        Strategy sub = new SubOperate();        context.setStrategy(sub);        System.out.println("相减Sub策略后返回结果："+context.doOperate(number1,number2));        Strategy multi = new MultiOperate();        context.setStrategy(multi);        System.out.println("相乘Multi策略后返回结果："+context.doOperate(number1,number2));    }}