package person.seven.designMode.behavior.TemplatePattern;/** * @description: * @author: liuqi create on 2018/8/14 **/public abstract class Game {    boolean whosyourDady = false;    protected void initGame(){        System.out.println("游戏初始化成功");    }    abstract void startPlay();    protected void stopGame(){        System.out.println("游戏结束，关机");    }    protected void invincible(){        System.out.println("敲出whosyourdaddy外挂，我是无敌的");    }    public void play(){        initGame();        startPlay();        if(whosyourDady){            invincible();        }        stopGame();    }    public void setWhosyourDady(boolean whosyourDady) {        this.whosyourDady = whosyourDady;    }}