package person.seven.designMode.construction.AdapterPattern;import static person.seven.designMode.construction.AdapterPattern.MediaEnum.*;/** * @description: * @author: liuqi create on 2018/8/14 **/public class AutoPlayer implements MediaPlayer{    MediaAdapter adapter;    @Override    public void play(MediaEnum mediaEnum, String fileName) {        if(mediaEnum == MP3){            System.out.println("自动播放器内置Mp3播放："+fileName);        }else if(mediaEnum == MP4 || mediaEnum == AVI){            adapter = new MediaAdapter(mediaEnum);            adapter.play(mediaEnum,fileName);        }else{            System.out.println("自动播放器不认识这个格式不播放："+fileName);        }    }}