package person.seven.designMode.construction.AdapterPattern;/** * @description: * @author: liuqi create on 2018/8/14 **/public class MediaAdapter implements MediaPlayer{    private MediaEnum type;    private AdvancedMediaPlayer player;    public MediaAdapter(MediaEnum type) {        this.type = type;        switch (type){            case MP4: player = new Mp4MediaPlayer();break;            case AVI: player = new AviMediaPlayer();break;            default:        }    }    @Override    public void play(MediaEnum audioType, String fileName) {        switch (audioType){            case MP4: player.playMp4(fileName);            case AVI: player.playAvi(fileName);            default:        }    }}