package person.seven.designMode.construction.DecoratorPattern;/** * @description: * @author: liuqi create on 2018/8/13 **/public class RedShapeDecorator extends ShapeDecorator {    public RedShapeDecorator(Shape decoratedShape) {        super(decoratedShape);    }    @Override    public void draw() {        decoratedShape.draw();        setRedBorder(decoratedShape);    }    private void setRedBorder(Shape decoratedShape){        System.out.println("Border Color: Red");    }}