package person.seven.designMode.construction.FacadePattern;/** * @description: * @author: liuqi create on 2018/8/13 **/public class Computer {    private Hardware cpu;    private Hardware hardDisk;    private Hardware memory;    public Computer() {        this.cpu = new Cpu();        this.hardDisk = new HardDisk();        this.memory = new Memory();    }    public void startCpu(){        cpu.operate();    }    public void startHardDisk(){        hardDisk.operate();    }    public void startMemory(){        memory.operate();    }}