package person.seven.designMode.construction.FilterPattern;import java.util.List;/** * @description: * @author: liuqi create on 2018/8/10 **/public class AndCriteria extends TypeCriteria {    private Criteria criteria;    private Criteria otherCriteria;    public AndCriteria(Criteria criteria, Criteria otherCriteria) {        this.criteria = criteria;        this.otherCriteria = otherCriteria;    }    @Override    public List<Person> meetCriteria(List<Person> persons) throws Exception {        List<Person> firstCriteriaPersons = criteria.meetCriteria(persons);        return otherCriteria.meetCriteria(firstCriteriaPersons);    }}