package person.seven.designMode.construction.FlyweightPattern;import com.google.common.collect.Maps;import java.util.Map;/** * @description: * @author: liuqi create on 2018/8/15 **/public class ShapeFactory {    private static Map<String,Shape> shapeList = Maps.newHashMap();    public static Shape getShape(String color){        Shape shape =  shapeList.get(color);        if(shape == null){            shape = new Circle(color);            shapeList.put(color,shape);        }        return shape;    }}