package person.seven.designMode.construction.ProxyPattern;/** * @description: * @author: liuqi create on 2018/8/16 **/public class ProxyImage implements Image {    private String fileName;    private RealImage image;    public ProxyImage(String fileName) {        this.fileName = fileName;    }    @Override    public void display() {        if(image == null){            image = new RealImage(fileName);        }        image.display();    }}