package person.seven.designMode.construction.ProxyPattern;/** * @description: * @author: liuqi create on 2018/8/16 **/public class RealImage implements Image {    private String fileName;    public RealImage(String fileName) {        this.fileName = fileName;        loadFile(fileName);    }    private void loadFile(String fileName) {        System.out.println("每次拉图片就要从磁盘加载一次，这次加载的是："+fileName);    }    @Override    public void display() {        System.out.println("你看，这次显示的图片是："+fileName);    }}