package person.seven.designMode.create.FactoryPattern;/** * @description: * @author: liuqi create on 2018/8/14 **/public enum  ShapeEnum {    CIRCLE("circle",new Circle()),SQUARE("square",new Square()),RECTANGLE("rectangle",new Rectangle());    private String key;    private Shape values;    ShapeEnum(String key, Shape values) {        this.key = key;        this.values = values;    }    public String getKey() {        return key;    }    public void setKey(String key) {        this.key = key;    }    public Shape getValues() {        return values;    }    public void setValues(Shape values) {        this.values = values;    }}