package person.seven.designMode.create.FactoryPattern;import static person.seven.designMode.create.FactoryPattern.ShapeEnum.*;/** * @description: * @author: liuqi create on 2018/8/14 **/public class ShapeFactory {    public Shape getShape(ShapeEnum type){        switch (type){            case CIRCLE:                return CIRCLE.getValues();            case SQUARE:                return SQUARE.getValues();            case RECTANGLE:                return RECTANGLE.getValues();            default:                System.out.println("工厂还不生产这个对象");                return null;        }    }}