package person.seven.designMode.create.PrototypePattern;/** * @description: 原型模式 * 在我看来，就是创建一个消耗资源的对象，缓存起来，每次需要用到就拷贝一份。不直接操作对象本身 * @author: liuqi create on 2018/8/14 **/public class Demo {    public static void main(String[] args) {        Shape clonedShape = (Shape) ShapeCache.getShape("1");        System.out.println("Shape : " + clonedShape.getType());        Shape clonedShape2 = (Shape) ShapeCache.getShape("2");        System.out.println("Shape : " + clonedShape2.getType());        Shape clonedShape3 = (Shape) ShapeCache.getShape("3");        System.out.println("Shape : " + clonedShape3.getType());    }}