package person.seven.designMode.create.PrototypePattern;/** * @description: * @author: liuqi create on 2018/8/14 **/public abstract class Shape  implements Cloneable{    private String id;    protected String type;    abstract void draw();    public String getType(){        return type;    }    public String getId() {        return id;    }    public void setId(String id) {        this.id = id;    }    @Override    protected Object clone()  {        Object shape = null;        try {            shape = super.clone();        } catch (CloneNotSupportedException e) {            e.printStackTrace();        }        return shape;    }}