package person.seven.designMode.create.PrototypePattern;import com.google.common.collect.Maps;import java.util.Map;/** * @description: * @author: liuqi create on 2018/8/14 **/public class ShapeCache {    public static Map<String,Shape> cacheMap = Maps.newHashMap();    static {        Shape circle = new Circle("1");        cacheMap.put(circle.getId(),circle);        circle = new Square("2");        cacheMap.put(circle.getId(),circle);        circle = new Rectangle("3");        cacheMap.put(circle.getId(),circle);    }    public static Shape getShape(String index){        Shape shape = cacheMap.get(index);        return (Shape) shape.clone();    }}