package person.seven.thread;/** * @description: * @author: liuqi create on 2018/7/17 **/public class InterruptRunnableDemo extends Thread {    @Override    public void run() {        while (!Thread.currentThread().isInterrupted()) {            System.out.println("线程执行中");        }        System.out.println("done ");    }    public static void main(String[] args) throws InterruptedException {        Thread thread = new InterruptRunnableDemo();        thread.start();        Thread.sleep(1000);        thread.interrupt();    }}