package person.seven.thread.future;import java.util.concurrent.TimeUnit;/** * @description: * @author: liuqi create on 2018/4/20 **/public class FutureTest {    public static void main(String[] args) throws InterruptedException {        Client client  = new Client();        Data data = client.request("(request)");        System.out.println("客户端:拿到的请求结果，但是去做了别的事情");        TimeUnit.SECONDS.sleep(1);        System.out.println("客户端:处理完了别的事情");        System.out.println(data.getResponse());    }}